OPENSSL_10_DH *ripcSSL10DHGetParam(ripcSSLApiFuncs* sslFuncs, ripcCryptoApiFuncs* cryptoFuncs)
{
	OPENSSL_10_DH *dh;

	if ((dh = (*(cryptoFuncs->dh_new_10))()) == NULL) return(NULL);
	dh->p = (*(cryptoFuncs->bin2bn))(dhp_4096, (int)sizeof(dhp_4096), NULL);
	dh->g = (*(cryptoFuncs->bin2bn))(dhg_4096, (int)sizeof(dhg_4096), NULL);
	if ((dh->p == NULL) || (dh->g == NULL))
	{
		(*(cryptoFuncs->dh_free_10))(dh); return(NULL);
	}
	return(dh);
}

OPENSSL_11_DH *ripcSSL11DHGetParam(ripcSSLApiFuncs* sslFuncs, ripcCryptoApiFuncs* cryptoFuncs)
{
	OPENSSL_11_DH *dh = (*(cryptoFuncs->dh_new_11))();
	OPENSSL_BIGNUM *dhp_bn, *dhg_bn;
	int i = -1;

	if (dh == NULL)
		return NULL;
	dhp_bn = (*(cryptoFuncs->bin2bn))(dhp_4096, sizeof(dhp_4096), NULL);
	dhg_bn = (*(cryptoFuncs->bin2bn))(dhg_4096, sizeof(dhg_4096), NULL);
	if (dhp_bn == NULL || dhg_bn == NULL
		|| !(i = (*(cryptoFuncs->dh_set0_pqg_11))(dh, dhp_bn, NULL, dhg_bn))) {
		(*(cryptoFuncs->dh_free_11))(dh);
		(*(cryptoFuncs->bn_free))(dhp_bn);
		(*(cryptoFuncs->bn_free))(dhg_bn);
		return NULL;
	}
	return dh;
}

